$("#rsa_encrypt").click(function(){var b=$("#rsa_ne").val(),a=$("#rsa_m").val(),c=/^\(((\d)+), ?((\d)+)\)$/;try{if(0==b.length)throw"Empty public key";if(0==a.length)throw"Empty message";if(!c.test(b))throw"Invalid public key encoding";if(parseInt(a)!=a)throw"Invalid message (must be an integer)";var d=c.exec(b),e=mod_exp(a,d[3],d[1]);$("#rsa_c").val(e);$("#rsa_output").show()}catch(f){return alert(f),!1}});
